import React from 'react';
import { motion } from 'framer-motion';
import '../css/Portfolio.css';

const projects = [
  {
    title: 'Proyecto 1',
    description: 'App para gestión de tareas',
    image: '/assets/proyecto1.png',
    link: 'https://github.com/usuario/proyecto1',
  },
  {
    title: 'Proyecto 2',
    description: 'Sitio web de ecommerce',
    image: '/assets/proyecto2.png',
    link: 'https://github.com/usuario/proyecto2',
  },
];

function Portfolio() {
  return (
    <section className="portfolio" id="portafolio">
      <h2>Portafolio</h2>
      <div className="projects">
        {projects.map((proj, index) => (
          <motion.div
            className="project-card"
            key={index}
            whileHover={{ scale: 1.05 }}
          >
            <img src={proj.image} alt={proj.title} />
            <h3>{proj.title}</h3>
            <p>{proj.description}</p>
            <a href={proj.link} target="_blank">Ver más</a>
          </motion.div>
        ))}
      </div>
    </section>
  );
}

export default Portfolio;
