// src/components/About.js
import React from 'react';
import { motion } from 'framer-motion';
import '../css/About.css';

function About() {
  return (
    <motion.section
      className="about"
      id="sobremi"
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <h2>Sobre mí</h2>
      <p>
        Soy un desarrollador con experiencia en la creación de aplicaciones web modernas usando React, Node.js y herramientas de diseño responsive.
        Me apasiona aprender nuevas tecnologías y colaborar en proyectos innovadores.
      </p>
    </motion.section>
  );
}

export default About;
