// Importa React para poder utilizar JSX
import React from 'react';

// Importa los estilos específicos de esta sección
import '../css/Contact.css';

// Componente funcional que representa la sección de contacto
function Contact() {
  return (
    // Sección principal con clase e ID, útil para anclas desde el navbar
    <section className="contact" id="contacto">

      {/* Título de la sección */}
      <h2>Contacto</h2>


      <form onSubmit={(e) => e.preventDefault()}>


        <input
          type="text"
          name="name"
          placeholder="Tu nombre"
          required // Campo obligatorio
        />


        <input
          type="email"
          name="email"
          placeholder="Tu email"
          required // Campo obligatorio
        />


        <textarea
          name="message"
          rows="5"
          placeholder="Tu mensaje"
          required // Campo obligatorio
        ></textarea>


        <button type="submit">Enviar</button>
      </form>
    </section>
  );
}

// Exporta el componente para que pueda ser utilizado en otros archivos como App.js
export default Contact;
