// Importa React para poder usar JSX
import React from 'react';

// Importa los estilos personalizados para esta sección
import '../css/Contact.css';

// Componente funcional que representa la sección de contacto
function Contact() {
  return (
    // Sección con clase CSS y un ID utilizado para navegación
    <section className="contact" id="contacto">


      <h2>Contacto</h2>

      {/* Formulario de contacto que envía los datos al servicio Formspree */}


        {/* Campo de texto para el nombre del usuario */}
        <input type="text" name="name" placeholder="Tu nombre" required />

        {/* Campo de correo electrónico */}
        <input type="email" name="email" placeholder="Tu email" required />

        {/* Área de texto para el mensaje */}
        <textarea name="message" rows="5" placeholder="Tu mensaje" required></textarea>

        {/* Botón para enviar el formulario */}
        <button type="submit">Enviar</button>

    </section>
  );
}

// Exporta el componente para ser utilizado en otros archivos (como App.js)
export default Contact;
